sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	//confirm("DJ client script onChange() set on Requested For field");
	//alert ('value of the control'+control);
	//alert ('value of the oldValue '+oldValue+ ' and new Value is ' +newValue);
	//alert ('value of isLoading' +isLoading+ ' and template is '+isTemplate);
	if (g_form.getValue('u_request_type') == 'hr'){

		g_form.addOption('u_what_needed','hr1','Humar Resources 1');
		g_form.addOption('u_what_needed','hr2','Humar Resources 2');

	}

	if (g_form.getValue('u_request_type') == 'facilities'){

		g_form.addOption('u_what_needed','facilities1','Facilities 1');
		g_form.addOption('u_what_needed','facilities2','Facilities 2');

	}

	if ( !g_form.isNewRecord() && isLoading) {

		g_form.setValue('u_what_needed',g_form.getValue('u_what_needed'));
	}

}
